graphql.query:
mutation {
  signup(input: {
    username: "test_user"
    email: "test@example.com"
    password: "test123456"
  }) {
    success
    message
    user {
      id
      username
      email
      created_at
    }
    token
  }
}

query {
  login(input: {
    usernameOrEmail: "test_user"
    password: "test123456"
  }) {
    success
    message
    user {
      id
      username
      email
    }
    token
  }
}

query {
  getAllEmployees {
    success
    message
    employees {
      id
      first_name
      last_name
      email
      gender
      designation
      salary
      date_of_joining
      department
      employee_photo
      created_at
      updated_at
    }
  }
}

query {
  getEmployeeById(eid: "REPLACE_WITH_EMPLOYEE_ID") {
    success
    message
    employee {
      id
      first_name
      last_name
      email
      gender
      designation
      salary
      date_of_joining
      department
      employee_photo
    }
  }
}

mutation {
  updateEmployee(
    eid: "REPLACE_WITH_EMPLOYEE_ID"
    input: {
      designation: "Senior Software Engineer"
      salary: 95000
      department: "Engineering"
    }
  ) {
    success
    message
    employee {
      id
      first_name
      last_name
      designation
      salary
      department
      updated_at
    }
  }
}

query {
  searchEmployees(designation: "Software Engineer") {
    success
    message
    employees {
      id
      first_name
      last_name
      designation
      department
      salary
    }
  }
}

query {
